import './Main.css';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      navigate('/LIA');
    }, 3000); // 3초 후 이동

    return () => clearTimeout(timer); // 언마운트 시 타이머 정리
  }, [navigate]);

  return (
    <div className="Main">
      <div className="Main__content">
        <div className="Main__title">LIA</div>
        <div className="Main__subtitle">Listen, Interact, Assist</div>
      </div>
      <div className="Main__guide">Loading...</div>
    </div>
  );
}

export default Main;
