import { useState } from 'react';
import './LiaPage.css';
import { GoogleOAuthProvider, useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';

function LiaPage() {
  const [userInfo, setUserInfo] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [fitData, setFitData] = useState(null);

  const login = useGoogleLogin({
    scope: 'https://www.googleapis.com/auth/fitness.activity.read https://www.googleapis.com/auth/fitness.body.read',
    onSuccess: async tokenResponse => {
      setAccessToken(tokenResponse.access_token);

      // 사용자 기본 정보 가져오기
      const userRes = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
        headers: {
          Authorization: `Bearer ${tokenResponse.access_token}`,
        },
      });

      setUserInfo(userRes.data);
      console.log('로그인 성공:', userRes.data);

      // 백엔드에 Google Fit 데이터 요청
      const serverRes = await axios.post('http://localhost:5000/api/fetch_google_fit', {
        access_token: tokenResponse.access_token,
        email: userRes.data.email,
      });

      setFitData(serverRes.data.data);  // Google Fit 데이터 저장
      console.log('Google Fit 데이터:', serverRes.data);
    },
    onError: error => {
      console.error('로그인 실패:', error);
    },
  });

  return (
    <GoogleOAuthProvider clientId="829026060536-f7dpc16930esthgnn97soleggvmv3o16.apps.googleusercontent.com">
      <div style={{ textAlign: 'center', marginTop: '10vh' }}>
        <h1>Welcome to the LIA page!</h1>
        <p>This is the second screen.</p>

        {!userInfo ? (
          <button onClick={() => login()} className="button">Google 로그인 및 Fit 연동</button>
        ) : (
          <div style={{ marginTop: '1rem' }}>
            <p><strong>환영합니다, {userInfo.name}님!</strong></p>
            <img src={userInfo.picture} alt="프로필" style={{ borderRadius: '50%', width: '50px' }} />
          </div>
        )}

        {fitData && (
          <div style={{ marginTop: '2rem' }}>
            <h3>Google Fit 걸음 수 데이터</h3>
            <pre>{JSON.stringify(fitData, null, 2)}</pre>
          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default LiaPage;
